package tutorialsPoint;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=1;
		
		switch(a) {
		case 1: 
			System.out.println("The value of variable is 1");
			break;
		case 2:
			System.out.println("The value of variable is 2");
			break;
		case 3:
			System.out.println("The value of variable is 3");
			break;
		case 4:
			System.out.println("The value of variable is 4");
			break;
			
		}
		

	}

}
